import { useRef, useEffect, useState } from "react";
import gsap from "gsap";
import styles from "./SlideInText.module.scss";

const SlideInText = ({ text }) => {
	const [charsRef, setCharsRef] = useState([]);

	useEffect(() => {
		setElRefs((elRefs) =>
			Array(arrLength)
				.fill()
				.map((_, i) => elRefs[i] || createRef())
		);
	}, [text]);
	useEffect(() => {
		console.log(charsRef.current);
		gsap.from(charsRef.current, {
			yPercent: 120,
			stagger: 0.2,
			duration: 1,
			ease: "back",
		});
	}, [charsRef]);
	let refIndex = 0;
	return (
		<span aria-label={text} role="heading">
			{text.split(" ").map((word, wordIndex) => (
				<span className={styles.word} aria-hidden="true" key={wordIndex}>
					{word.split("").map((char, index) => {
						refIndex++;
						return (
							<span
								ref={charsRef.current[refIndex - 1]}
								key={index}
								className={styles.char}
							>
								{char}
							</span>
						);
					})}
				</span>
			))}
		</span>
	);
};

export default SlideInText;
