import gsap from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
gsap.registerPlugin(ScrollTrigger);

export const textIntro = (elem) => {
	gsap.from(elem, {
		xPercent: -20,
		opacity: 0,
		stagger: 0.2,
		duration: 2,
		scale: -1,
		ease: "back",
	});
};

export const enlargeUponScroll = (elem, trigger) => {
	const tl = gsap.timeline({
		scrollTrigger: {
			trigger: trigger,
			start: "top top",
			end: "bottom bottom",
			scrub: true,
		},
	});
	tl.to(elem, { scale: 1.1, ease: "none" });
};
