import Image from "next/image";
import { RichText } from "prismic-reactjs";
import styles from "./CardSection.module.scss";
import grid from "../../styles/grid.module.scss";

const CardSection = ({ slice }) => {
	return (
		<section
			className={`${styles.section} ${grid.loop} ${grid["loop--lg"]} ${grid.inner}`}
		>
			{slice.items.map((item, index) => (
				<article
					key={`card-${index}`}
					className={`
                        ${styles.card} 
                        ${item.negative ? styles.negative : ""} 
                        ${styles[`size--${item.card_size}`]}
                    `}
				>
					<h2 className={styles["card__heading"]}>{item.title}</h2>
					{item.image.url && (
						<Image
							className={styles["card__img"]}
							src={item.image.url}
							width={item.image.dimensions.width}
							height={item.image.dimensions.height}
							layout="responsive"
						/>
					)}

					<div className={styles["card__body"]}>
						{RichText.render(item.bodytext)}
					</div>
					<div className={styles["card__cta"]}>
						<button type="button">{item.cta_text}</button>
					</div>
				</article>
			))}
		</section>
	);
};

export default CardSection;
