import React, { useState } from "react";
import {
	EmailSignup,
	FullWidthImage,
	HeadlineWithButton,
	InfoWithImage,
	TextInfo,
	Hero,
	CardSection,
} from "./slices";
import { disableBodyScroll, enableBodyScroll } from "body-scroll-lock";
import { Popup } from "components/Popup/Popup";

const SliceZone = ({ sliceZone }) => {
	const [popup, setPopup] = useState();
	const openPopup = (content) => {
		setPopup(content);
		if (content) {
			disableBodyScroll();
		} else {
			enableBodyScroll();
		}
	};
	const closePopup = () => setPopup(null);

	return (
		<>
			{sliceZone.map((slice, index) => {
				switch (slice.slice_type) {
					case "hero":
						return <Hero slice={slice} key={`slice-${index}`} />;
					case "card_section":
						return (
							<CardSection
								handleCta={openPopup}
								slice={slice}
								key={`slice-${index}`}
							/>
						);
					case "text_info":
						return <TextInfo slice={slice} key={`slice-${index}`} />;
					case "info_with_image":
						return <InfoWithImage slice={slice} key={`slice-${index}`} />;
					case "full_width_image":
						return <FullWidthImage slice={slice} key={`slice-${index}`} />;
					case "headline_with_button":
						return <HeadlineWithButton slice={slice} key={`slice-${index}`} />;
					case "email_signup":
						return <EmailSignup slice={slice} key={`slice-${index}`} />;
					default:
						return null;
				}
			})}
			{popup && <Popup handleClose={closePopup} content={popup} />}
		</>
	);
};

export default SliceZone;
