import { createRef, useEffect, useState, useRef } from "react";
import gsap from "gsap";
import { ScrollTrigger } from "gsap/dist/ScrollTrigger";
import styles from "./SlideInText.module.scss";

const SlideInText = ({ text }) => {
	const [charsRef, setCharsRef] = useState([]);
	const triggerRef = useRef();

	useEffect(() => {
		setCharsRef((charsRef) =>
			Array(text.split("").length)
				.fill()
				.map((_, i) => charsRef[i] || createRef())
		);
	}, [text]);

	useEffect(() => {
		gsap.from(
			charsRef.map((char) => char.current),
			{
				yPercent: 120,
				stagger: 0.05,
				duration: 1,
				ease: "power3.out",
				scrollTrigger: {
					markers: true,
					trigger: triggerRef.current,
					start: "top top",
					end: "bottom bottom",
				},
			}
		);
	}, [charsRef]);

	let refIndex = 0;
	return (
		<span ref={triggerRef} aria-label={text} role="heading">
			{text.split(" ").map((word, wordIndex) => (
				<span className={styles.word} aria-hidden="true" key={wordIndex}>
					{word.split("").map((char, index) => {
						refIndex++;
						return (
							<span
								ref={charsRef[refIndex - 1]}
								key={index}
								className={styles.char}
							>
								{char}
							</span>
						);
					})}
				</span>
			))}
		</span>
	);
};

export default SlideInText;
