import { useRef, useEffect } from "react";
import gsap from "gsap";
import { default as splitting } from "splitting/dist/splitting";

const SlideInText = ({ children }) => {
	const textRef = useRef();
	useEffect(() => {
		splitting({ target: textRef.current });
		gsap.from(textRef.current.childNodes, {
			yPercent: 120,
			stagger: 0.2,
			duration: 1,
			ease: "back",
		});
	}, [textRef]);
	return <div ref={textRef}>{children}</div>;
};

export default SlideInText;
