import { useRef, useEffect } from "react";
import gsap from "gsap";
import styles from "./SlideInText.module.scss";

const SlideInText = ({ text }) => {
	const charsRef = useRef([]);
	charsRef.current = new Array(text.split("").length).map(
		(ref, index) => (usernameRefs.current[index] = React.createref())
	);

	useEffect(() => {
		gsap.from(charsRef.current, {
			yPercent: 120,
			stagger: 0.2,
			duration: 1,
			ease: "back",
		});
	}, [charsRef]);
	let refIndex = 0;
	return (
		<span aria-label={text} role="heading">
			{text.split(" ").map((word, wordIndex) => (
				<span className={styles.word} aria-hidden="true" key={wordIndex}>
					{word.split("").map((char, index) => {
						refIndex++;
						console.log(refIndex);
						return (
							<span
								ref={charsRef.current[refIndex - 1]}
								key={index}
								className={styles.char}
							>
								{char}
							</span>
						);
					})}
				</span>
			))}
		</span>
	);
};

export default SlideInText;
