import Image from "next/image";
import { RichText } from "prismic-reactjs";
import hero from "./Hero.module.scss";
import grid from "../../styles/grid.module.scss";

const Hero = ({ slice }) => {
	const { width, height } = slice.primary.bg.dimensions;
	return (
		<section className={`${hero.section} ${grid.col}`}>
			<div className={hero.img}>
				<Image
					src={slice.primary.bg.url}
					layout="fill"
					objectFit="cover"
					objectPosition="center"
					loading="eager"
				/>
			</div>
			<header className={`${hero.header}`}>
				<h1 className="t--1">{slice.primary.headline}</h1>
			</header>
			<div className={hero.body}>{RichText.render(slice.primary.bodytext)}</div>
		</section>
	);
};

export default Hero;
