import Image from "next/image";
import { RichText } from "prismic-reactjs";

const Hero = ({ slice }) => {
	const { width, height } = slice.primary.bg.dimensions;
	return (
		<section>
			<Image
				src={slice.primary.bg.url}
				width={width}
				height={height}
				layout="responsive"
			/>
			<header>{RichText.render(slice.primary.headline)}</header>
			{RichText.render(slice.primary.bodytext)}
		</section>
	);
};

export default Hero;
