import { useRef, useEffect } from "react";
import gsap from "gsap";
import styles from "./SlideInText.module.scss";

const SlideInText = ({ text }) => {
	const charsRef = useRef([]);
	useEffect(() => {
		gsap.from(textRef.current.firstChild.childNodes, {
			yPercent: 120,
			stagger: 0.2,
			duration: 1,
			ease: "back",
		});
	}, [charsRef]);
	return (
		<span aria-label={text} role="heading">
			{text.split(" ").map((word, index) => (
				<span className={styles.word} aria-hidden="true" key={index}>
					{word.split("").map((char, index) => (
						<span key={index} className={styles.char}>
							{char}
						</span>
					))}
				</span>
			))}
		</span>
	);
};

export default SlideInText;
