import Image from "next/image";
import { RichText } from "prismic-reactjs";
import styles from "./CardSection.module.scss";
import grid from "../../styles/grid.module.scss";
import Button from "components/Button";
import Masonry from "react-masonry-css";

const CardSection = ({ slice, handleCta }) => {
	const handleLoad = (e) => {
		if (e.target.srcset) {
			console.log("loaded", e.target.scrset);
		}
	};

	return (
		<section className={`${styles.section} ${grid.inner}`}>
			<Masonry
				breakpointCols={{ default: 4, 1100: 3, 700: 2, 500: 1 }}
				className={styles.masonry}
				columnClassName={styles["masonry__column"]}
			>
				{slice.items.map((item, index) => (
					<article
						key={`card-${index}`}
						className={`
                        ${styles.card} 
                        ${item.negative ? styles.negative : ""} 
                        ${
													item.card_size
														? styles[`size--${item.card_size}`]
														: ""
												}
                    `}
					>
						<h2 className={styles["card__heading"]}>{item.title}</h2>
						{item.image.url && (
							<div className={styles["card__img"]}>
								<Image
									src={item.image.url}
									width={item.image.dimensions.width}
									height={item.image.dimensions.height}
								/>
							</div>
						)}

						<div className={styles["card__body"]}>
							{RichText.render(item.bodytext)}
						</div>
						<div className={styles["card__cta"]}>
							<button
								onClick={(e) => {
									e.preventDefault;
									handleCta(item.popup);
								}}
								type="button"
							>
								{item.cta_text}
							</button>
						</div>
					</article>
				))}
			</Masonry>
		</section>
	);
};

export default CardSection;
